@import url('https://fonts.googleapis.com/css2?family=Montserrat:ital,wght@0,200;0,300;0,400;0,500;0,700;0,800;1,400;1,500&family=Open+Sans:ital,wght@0,300;0,400;0,600;0,700;0,800;1,400&display=swap');

// font-family: 'Montserrat', sans-serif;
// font-family: 'Open Sans', sans-serif;

html {
    font-family: 'Open Sans', sans-serif;
}

h1,
h2,
h3 {
    font-family: 'Montserrat', sans-serif;
    font-weight: 500;
}

h1 {
    font-size: 2.5rem;
}

h2 {
    font-size: 2rem;
}

h3 {
    font-size: 1.7rem;
}

article {
    h5 {
        font-size: 1.1rem;
        font-weight: 600;
    }

    ul,
    ol {
        font-size: 1.15rem;
    }

    & > *:not(h1, h2, h3){
        font-size: 1rem;
    }
}

article p,
.comment__content {
    line-height: 1.6;
}

.comment {

    &__username {
        font-size: 1.3rem;
        font-weight: 600;
    }

    &__date {
        font-size: 1rem;
    }
}

.logo {
    font-size: 1.7rem;
    font-weight: 300;
}

.nav__btn,
.theme_change {
    font-size: 2rem;
}

.newest_articles__image > p{
    font-size: 1.5rem;
    font-weight: 600;
}

.footer {
    strong {
        font-size: 1.3rem;
        font-family: 'Montserrat', sans-serif;
        font-weight: 600;
        letter-spacing: 1.5px;
    }
}

.to_top {
    font-size: 1.5rem;
}

@media (max-width: 320px) {
    html {
        font-size: .8rem;
    }

    h1 {
        font-size: 2.2rem;
    }

    h2 {
        font-size: 1.7rem;
    }

    h3 {
        font-size: 1.4rem;
    }
}

@media (max-width: 600px) {
    .nav__link {
        font-size: 1.3rem;
        text-align: center;
    }
}

