@use '../utilities' as *;

.comments {
    padding: 1.5em 1em;
    background-color: var(--bg5);
    border-radius: 1rem;

    &__header {
        @include fancy_font_right (var(--bg3), 4px);
        margin: 2em .7em 1em;
