        textarea,
        label {
            display: block;
        }

        label {
            margin: 1em 0;
        }

        input[type=text],
        textarea {
            width: 100%;
            margin-top: .7rem;
            border: none;
            outline-color: $accent-400;
        }

        input[type=text] {
            padding: .7em 1em;
            font-size: 1rem;
            border-radius: 9999px;
        }

        textarea {
            width: 100%;
            min-height: 10vh;
            max-height: 60vh;
            padding: 1em;
            font-size: 1.1rem;
            border-radius: 1rem 1rem 0 1rem;
            resize: vertical;
        }

        button {
            @include button (100%, $primary-800);
        }
    }
}

.comment {
    padding: 1em;

    // &__header {}
    // &__username {}

    &__date {
        opacity: .75;
    }

    &__content {
        margin-top: .5em;
    }

    & + & {
        border-top: 2px dashed $neutral-100;
    }

    &:first-of-type {
        padding-top: 1.5em;
    }

    &:last-of-type {
        padding-bottom: 0;
    }
