@use '../utilities' as *;

.articles {
    background-color: var(--bg1);
    overflow-x: hidden;
    @include scroll_bar;
    @include paddings;

    .articles__list {
        min-height: 100vh;
        margin: auto;
        background-color: var(--bg4);

        .article {
            display: flex;
            justify-content: space-between;
            transition: background-color .4s ease-in;
            cursor: pointer;

            img {
                object-fit: cover;
                border-radius: 15px;
            }

            &:hover {
                background-color: var(--bg3);
            }

            & + .article {
                border-top: 2px dashed var(--bg1);
            }

            &__text_preview {
                margin-top: 1em;
            }
        }

        @media (max-width: 900px) {
            .article {
                flex-direction: column-reverse;
                justify-content: center;
                padding: 2rem 1.5em;

                & > div {
                    width: 90%;
                    margin: auto;
                    margin-top: 1.5em;
                }

                img {
                    width: 90%;
                    max-height: 300px;
                    margin: auto;
                }
            }
        }

        @media (min-width: 900px) {
            margin: 3rem auto;
            border-radius: 20px;
            overflow: hidden;

            .article {
                padding: 3rem;

                & > div {
                    align-self: center;
                    width: 50%;
                }

                p {
                    max-width: 65ch;
                }

                img {
                    width: 35%;
                    max-width: 600px;
                    max-height: 250px;
                }
            }
        }

        @media (min-width: 1000px) {
            width: 80%;
        }
    }

    article {
        position: relative;
        max-width: 1000px;
        min-height: 100vh;
        margin: auto;
        padding: 1em 1em 5rem;

        h1, h2 {
            margin-bottom: .5em;
        }

        h3 {
            margin-bottom: .3em;
        }

        h5 {
            margin: 1rem 0 2.5rem 0;
        }

        p + h2 {
            margin-top: 1.5em;
        }

        p + h3 {
            margin-top: 1rem;
        }

        p + p {
            margin-top: 1em;
        }

        blockquote {
            margin: 1rem 0;
            padding: 1rem;
            border: 4px solid var(--bg3);
            border-radius: 15px;

            .cite_src {
                margin-right: .5em;
                margin-bottom: -.3em;
                text-align: right;
            }
        }

        ul,
        ol {
            margin: 1em 0;
            padding-left: 1.5rem;

            li + li {
                margin-top: .5rem;
            }
        }

        hr {
            margin: .5rem 0 1rem;
            border: 2px solid var(--bg3);
        }

        .image {
            position: relative;

            img {
                width: 100%;
                min-height: 450px;
                height: 50vh;
                object-fit: cover;
                border-radius: 1em;
            }

            &--title img {
                height: 350px;
            }

            &:not(.image--title) {
                margin: 2rem 0;
            }

            &[data-showSource="true"]::after{
                content: attr(data-ImgSource);
                position: absolute;
                bottom: 1em;
                right: 1em;
                padding: .3em;
                background-color: rgba($neutral-900, .5);
                color: $neutral-100;
                border-radius: 5px;
                font-size: .9rem;
            }
        }

        .video_container {
            position: relative;
            width: 100%;
            height: 0;
            margin: 2rem 0;
            padding-bottom: 56.25%;

            iframe {
                position: absolute;
                top: 0;
                left: 0;
                width: 100%;
                height: 100%;
            }
        }

        .redirect {
            color: $primary-800;
            font-weight: 700;
            text-decoration: underline;
            cursor: pointer;
        }

        .date {
            opacity: .7;
        }

        .sources {
            position: absolute;
            bottom: 1em;
            left: 1em;
            max-width: 90vw;
            overflow-x: auto;

            @media (min-width: 600px) {
                max-width: 75vw;
            }
        }

        .source {
            margin-left: .4em;
        }

        .back {
            margin: .5rem 0;
            color: var(--text-color);
            font-size: 1.5rem;
            cursor: pointer;

            &:hover {
                opacity: .5;
            }
        }

        @media (min-width: 600px) {
            font-size: 1.3rem;
        }

    }

    .to_top {
        display: grid;
        place-items: center;
        position: fixed;
        bottom: 15px;
        right: 15px;
        width: 3rem;
        height: 3rem;
        background-color: $primary-400;
        color: $neutral-100;
        border-radius: 100%;
        box-shadow: .2rem .2rem 4px 1px rgba(#000, 0.7);
        opacity: 0;
        z-index: 5;
        cursor: pointer;
        transition: all .7s ease-in-out;

        &:hover {
            background-color: $primary-800;
            box-shadow: -.2rem -.2rem 4px 1px rgba(#000, 0.7);
        }
    }

    #articles_container {
        background-color: var(--bg2);
    }
}

.articles.dark {
    img {
        opacity: .8;

        &:hover {
            opacity: 1;
        }
    }
}

