@use '../utilities/colors' as *;

@mixin scroll_bar {
	&::-webkit-scrollbar-track {
		background-color: $neutral-200;
	}

	&::-webkit-scrollbar {
		width: 12px;
	}

	&::-webkit-scrollbar-thumb {
		background-color: $neutral-800;
		border-radius: 10px;
	}

}

@mixin paddings{
	padding-top: 65px;

	@media (max-width: 1000px) {
		padding-top: 103px;
	}

	@media (max-width: 600px) {
		padding-top: 84px;
	}

	@media (max-width: 457px) {
		padding-top: 98px;
	}

	@media (max-width: 320px) {
		padding-top: 77px;
	}

	@media (max-width: 223px) {
		padding-top: 104px;
	}
}

//this isn't used, but could be
@mixin fancy_font ($color, $thickness) {
	margin-left: 0;
	margin-right: 0;
	text-align: center;
	line-height: 0.5;

	span {
		display: inline-block;
		position: relative;

		&::before,
		&::after {
			content: '';
			position: absolute;
			top: 50%;
			height: 0;
			border-top: $thickness solid $color;
			width: calc(50vw - 100%);
		}

		&:before {
			right: 100%;
			margin-right: 15px;
		}

		&:after {
			left: 100%;
			margin-left: 15px;
		}
	}
}

@mixin fancy_font_right ($color, $thickness, $font: 1.7rem) {
	@media (min-width: 320px) {
		display: flex;
		height: $font + .5rem;
		margin-left: 0;
		margin-right: 0;
		line-height: 0.5;
		overflow-x: hidden;

		span {
			display: inline-block;
			align-self: center;
			position: relative;

			&::after {
				content: '';
				position: absolute;
				top: 50%;
				left: 100%;
				height: 0;
				margin-left: 15px;
				border-top: $thickness solid $color;
				width: calc(100vw - 100%);
				max-width: calc(1000px - 100%);
			}
		}
	}
}

@mixin button ($width, $color) {
	width: $width;
	margin: auto;
	padding: .7em 1em;
	background-color: $color;
	color: $neutral-100;
	font-size: 1.1rem;
	border: none;
	border-radius: 9999px;
	cursor: pointer;

	&:hover {
		opacity: .85;
	}

	i.fas {
		margin-right: .6rem;
	}
}

