        @media (max-width: 900px) {
            .article {
                flex-direction: column-reverse;
                justify-content: center;
                padding: 2rem 1.5em;

                & > div {
                    width: 90%;
                    margin: auto;
                    margin-top: 1.5em;
                }

                img {
                    width: 90%;
                    max-height: 300px;
                    margin: auto;
                }
            }
        }

        @media (min-width: 900px) {
            margin: 3rem auto;
            border-radius: 20px;
