            .article {
                padding: 3rem;

                & > div {
                    align-self: center;
                    width: 50%;
                }

                p {
                    max-width: 65ch;
                }

                img {
                    width: 35%;
                    max-width: 600px;
                    max-height: 250px;
                }
            }
        }

        @media (min-width: 1000px) {
            width: 80%;
        }
    }

    article {
        position: relative;
        max-width: 1000px;
        min-height: 100vh;
        margin: auto;
        padding: 1em 1em 5rem;

        h1, h2 {
            margin-bottom: .5em;
        }

        h3 {
            margin-bottom: .3em;
        }

        h5 {
            margin: 1rem 0 2.5rem 0;
        }

        p + h2 {
            margin-top: 1.5em;
        }

        p + h3 {
            margin-top: 1rem;
        }

        p + p {
            margin-top: 1em;
        }

        blockquote {
            margin: 1rem 0;
            padding: 1rem;
            border: 4px solid var(--bg3);
            border-radius: 15px;

            .cite_src {
                margin-right: .5em;
                margin-bottom: -.3em;
                text-align: right;
            }
        }

        ul,
        ol {
            margin: 1em 0;
            padding-left: 1.5rem;

