                margin-top: .5rem;
            }
        }

        hr {
            margin: .5rem 0 1rem;
            border: 2px solid var(--bg3);
        }

        .image {
            position: relative;

            img {
                width: 100%;
                min-height: 450px;
                height: 50vh;
                object-fit: cover;
                border-radius: 1em;
            }

            &--title img {
                height: 350px;
            }

            &:not(.image--title) {
                margin: 2rem 0;
            }

            &[data-showSource="true"]::after{
                content: attr(data-ImgSource);
                position: absolute;
                bottom: 1em;
                right: 1em;
                padding: .3em;
                background-color: rgba($neutral-900, .5);
                color: $neutral-100;
                border-radius: 5px;
                font-size: .9rem;
            }
        }

