            cursor: pointer;

            &:hover {
                opacity: .5;
            }
        }

        @media (min-width: 600px) {
            font-size: 1.3rem;
        }

    }

    .to_top {
        display: grid;
        place-items: center;
        position: fixed;
        bottom: 15px;
        right: 15px;
        width: 3rem;
        height: 3rem;
        background-color: $primary-400;
        color: $neutral-100;
        border-radius: 100%;
        box-shadow: .2rem .2rem 4px 1px rgba(#000, 0.7);
        opacity: 0;
        z-index: 5;
        cursor: pointer;
        transition: all .7s ease-in-out;

        &:hover {
            background-color: $primary-800;
            box-shadow: -.2rem -.2rem 4px 1px rgba(#000, 0.7);
        }
    }

    #articles_container {
        background-color: var(--bg2);
    }
}

.articles.dark {
    img {
        opacity: .8;

        &:hover {
            opacity: 1;
        }
    }
}

